#ifndef LTZ_PREPROCESSOR_HPP
#define LTZ_PREPROCESSOR_HPP

#include <boost/preprocessor.hpp>

#define _LTZ_PP_CAT_WITH_SEP_IMPL_OP(s, sep, elem) BOOST_PP_CAT(sep, elem)
#define _LTZ_PP_CAT_WITH_SEP_IMPL(sep, ...) BOOST_PP_SEQ_CAT(BOOST_PP_SEQ_PUSH_FRONT(BOOST_PP_SEQ_POP_FRONT(BOOST_PP_SEQ_TRANSFORM(_LTZ_PP_CAT_WITH_SEP_IMPL_OP, sep, BOOST_PP_VARIADIC_TO_SEQ(__VA_ARGS__))), BOOST_PP_VARIADIC_ELEM(0, __VA_ARGS__)))


/* export */

#define LTZ_PP_CAT_WITH_SEP(sep, ...) _LTZ_PP_CAT_WITH_SEP_IMPL(sep, __VA_ARGS__)

#define LTZ_PP_CAT_WITH_UNDERLINE(...) LTZ_PP_CAT_WITH_SEP(_, __VA_ARGS__)

#endif